%spark
import  model.v1._
import com.gigaspaces.async.AsyncResult
import org.openspaces.core.executor.DistributedTask
import org.insightedge.scala.annotation._
import scala.collection.JavaConversions._
import org.slf4j.{Logger, LoggerFactory}
import org.openspaces.core.GigaSpace
import org.openspaces.core.executor.TaskGigaSpace
import scala.beans.{BeanProperty}
import com.gigaspaces.document.SpaceDocument
import scala.collection.mutable.ListBuffer
import com.j_spaces.core.client.SQLQuery


class MergeTask extends DistributedTask[java.lang.Integer, java.lang.Integer] {
  private val logger = LoggerFactory.getLogger(classOf[MergeTask])
  @TaskGigaSpace @transient private val gs: GigaSpace = null

override def execute(): Integer = {
    if(gs != null) {
      val clusteredProxy = gs.getClustered()
      logger.info("execute returned from: " + gs.getSpaceName)
        val template =new SQLQuery[FlightDelaysWithWeather](classOf[FlightDelaysWithWeather], "tmin is null")
        val weatherTemplate =new SpaceDocument("Weather");
        var writtenCount:Integer = 0
        var retArray:Array[FlightDelaysWithWeather] = null
        logger.info("Created template")
        try {
            while ( {retArray = gs.takeMultiple(template, 1000);retArray  != null && retArray.length > 0}) {
                var writeCollection = new ListBuffer[FlightDelaysWithWeather]
                for (rec <- retArray) {
                  val dateField:Int = ("%d%02d%02d".format(rec.year.toInt, rec.month.toInt, rec.dayofMonth.toInt)).toInt
                  val origin:String =  rec.origin
                  weatherTemplate.setProperty("Date", dateField)
                  weatherTemplate.setProperty("Station", origin)
                  val weather = clusteredProxy.read(weatherTemplate)
                  if (weather != null) {
                    rec.awnd = "" + weather.getProperty("AWND")
                    rec.prcp = "" + weather.getProperty("PRCP")
                    rec.snow = "" + weather.getProperty("SNOW")
                    rec.tmin = "" + weather.getProperty("TMIN")
                    rec.tmax = "" + weather.getProperty("TMAX")
                    rec.date =  dateField
                    writeCollection += rec
                  }
                }
                if(writeCollection.length > 0) {
                    gs.writeMultiple(writeCollection.toArray)
                } 
                writtenCount += writeCollection.length
            }
        } catch {
            case e: Exception => logger.warn(e.getMessage, e)
        }
      logger.info("Finished task " + writtenCount)

      return writtenCount
    } else {
      logger.info("gs Proxy not available")
        return -1

    }
  }
  override def reduce(results: java.util.List[AsyncResult[Integer]]): java.lang.Integer = {
    logger.info("In Reduce")
    return results.map(_.getResult().intValue()).sum
  }}
