%define
package model.v1

import com.gigaspaces.metadata._
import com.gigaspaces.metadata.index.SpaceIndexType;
import java.lang
import scala.beans.{BeanProperty}
import org.insightedge.scala.annotation._
import org.insightedge.spark.implicits.all._


//Describe the data as Scala Case Class

case class FlightDelaysWithWeather(
  @BeanProperty 
  @SpaceId
  var id: String,
  @BeanProperty 
  @SpaceIndex
  var carrier: String,
  @BeanProperty 
  @SpaceIndex
  var flight_number: String,
  @SpaceIndex
  @BeanProperty 
  var year: Integer,
  @BeanProperty 
  var month: String,
  @BeanProperty 
  var dayofMonth: String,
  @BeanProperty 
  var dayOfWeek: String,
  @BeanProperty 
  var crsDepTime: String,
  @SpaceIndex
  @BeanProperty 
  var depDelay15: java.lang.Double,
  @BeanProperty 
  var depDelay: java.lang.Double,
  @SpaceIndex
  @BeanProperty 
  var origin: String,
  @BeanProperty 
  var dest: String,
  @BeanProperty 
  var awnd: String,
  @BeanProperty 
  var prcp: String,
  @BeanProperty 
  var snow: String,
  @BeanProperty 
  var tmax: String,
  @BeanProperty 
  var tmin: String,
  @BeanProperty 
  var cancelled: String,
  @BeanProperty 
  var date: Integer,
  @BeanProperty 
  var prediction: String) {
  def this() = this(null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null, null)
  def generate_id() = {id = "%04d:%s:%s:%s:%s:%s".format(year, month, dayofMonth, dayOfWeek, crsDepTime, flight_number)}
}

%spark
//explore data before we continue
//WN - SouthWest, OO - SkyWest Airlines Inc., "EV" - ExpressJet Airlines LLC, "B6" - JetBlue, "AS" Alaska Airlines Inc. "NK" - Spirit Air Lines", "F9" - "Frontier Airlines Inc."
val airlines = List ("UA","OO","B6","NK", "F9")
// ORD - Chicago, DFW - Dallas, DEN - Denver, SFO - San Francisco
val airports = List ("ORD","DFW","DEN","SFO")
val flightDelaysDataFrame = sqlContext.read.option("header", "true").option("inferschema", "true").csv("/opt/gigaspaces/flightdelays20172018.csv").filter($"Reporting_Airline".isin(airlines:_*)).filter($"Origin".isin(airports:_*)).filter($"cancelled" === 0.0).filter($"DayofMonth" < 20.0)
flightDelaysDataFrame.printSchema()
// Create a temp view that we can query by SQL
flightDelaysDataFrame.createOrReplaceTempView("FlightDelays")
